    def addList(self):
        con = pymysql.connect(host="localhost", database="water",user="root", password="12345678", charset="utf8")
        cursor = con.cursor()
        print('fetching data')
        selectquery = "select * from shoesdb"
        cursor.execute(selectquery)
        data = cursor.fetchall()
        print(data)
        tablerow = 0
        for i in data:
            self.tableWidget.insertRow(tablerow)
            self.tableWidget.setItem(tablerow,0,QtWidgets.QTableWidgetItem(i[0]))
            self.tableWidget.setItem(tablerow,1,QtWidgets.QTableWidgetItem(str(i[1])))
            self.tableWidget.setItem(tablerow,2,QtWidgets.QTableWidgetItem(str(i[2])))
            self.tableWidget.setItem(tablerow,3,QtWidgets.QTableWidgetItem(str(i[3])))
            tablerow += 1
        cursor.close()
        con.close()

    def addsmoothie(self):
        con = pymysql.connect(host="localhost", database="water",user="root", password="12345678", charset="utf8")
        cursor = con.cursor()
        id = self.idle.text()
        name = self.namesle.text()
        taste = self.tastesle.text()
        cost = self.costsle.text()
        cursor.execute('insert into shoesdb(ID,NAME,TASTE,COST)values(%s,%s,%s,%s)',(id,name,taste,cost))
        con.commit()
        cursor.close()
        con.close()
        
